function ShowLoader(){$("#loader").hasClass("hidden")&&$("#loader").toggleClass("hidden")}function HideLoader(){$("#loader").hasClass("hidden")||$("#loader").toggleClass("hidden")}function GetHashParams(){const params={};let e,r=/([^&;=]+)=?([^&;]*)/g,q=window.location.hash.substring(1);while(e=r.exec(q))params[e[1]]=decodeURIComponent(e[2]);return params}function GetUrlParams(){const params={};for(const param of GlobalUrlParams)params[param[0]]=param[1];return params}function GetAllParams(){const urlParams=GetUrlParams(),hashParams=GetHashParams();return Object.assign(urlParams,hashParams)}function CalculateUnixInMsExpiry(expiresInSeconds){return(new Date).valueOf()+expiresInSeconds*1e3-1e3}const GlobalUrlParams=new URLSearchParams(window.location.search);